# Define a tighter function to check for strict magic square conditions
def is_strict_magic(grid, tolerance=1e-6):
    unique_vals = np.unique(grid.round(decimals=6))
    if len(unique_vals) != grid.size:
        return False  # Values must be unique

    target_sum = np.sum(grid[0])
    for i in range(3):
        if not np.isclose(np.sum(grid[i]), target_sum, atol=tolerance):
            return False
        if not np.isclose(np.sum(grid[:, i]), target_sum, atol=tolerance):
            return False
    if not np.isclose(np.sum(np.diag(grid)), target_sum, atol=tolerance):
        return False
    if not np.isclose(np.sum(np.diag(np.fliplr(grid))), target_sum, atol=tolerance):
        return False
    return True

