# Inverse mapping for reverse lookup
value_to_letter = {v: k for k, v in letter_to_value.items()}

# Function to find combinations of letters that sum to a target value
def find_letter_combinations(target, max_length=6):
    values = list(value_to_letter.keys())
    valid_combinations = []

